import React, { useState, useEffect } from 'react'
import SearchBar from './SearchBar.jsx'
import { Link } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { getPokemonsAttack, FilterByABC, getTypes} from '../store/actions/index.js'
import { useSelector } from 'react-redux'

export default function Nav() {
  let allTypes = useSelector((state) => state.types)
  let dispatch = useDispatch()
  console.log(allTypes)
  useEffect(() => {
    dispatch(getTypes())
  }, [dispatch])


  function firstFilter(event) {
    event.preventDefault()
    console.log(event.target.value)
    if (event.target.value === "Attack+") {
    dispatch(getPokemonsAttack())
  }
    else if (event.target.value === "Attack-") {
      dispatch(FilterByABC())
    }
  }

  function filterType(event) {
    event.preventDefault()
    console.log(event.target.value)
  }
  
  return (

    <div>
        <Link to='/create'><button>Create your own Pokemon</button></Link>
        
        <select defaultValue='Order By...' onChange={(e) => firstFilter(e)}>
          <option enable>Order Attack by...</option>
          <option value="Attack+">highest to lowest </option>
          <option value="Attack-">lowest to highest </option>
        </select>


        <select defaultValue='Order By...' onChange={secondFilter}>
          <option enable>Pokemons...</option>
          <option value="all">All</option>
          <option value="Created by you">Created by you</option>
        </select>

          
        <select onChange={filterType}>
          <option enable>Order By type...</option>
          {allTypes.sort((b, a) => a.type - b.type).map(types => {
            return <option value={types.type}>{types.type}</option>
          })}
        </select>
        
  
        <div>
            <SearchBar/>
        </div>
    
    </div>


  )
}
