                payload: pokemonsById
            })
        })
    }
}; */

export const getPokeDetail = (id) => async dispatch => {
    try {
        const json = await axios(`http://localhost:3001/pokemons/${id}`)
        return dispatch({ type: 'REQUEST_POKEMONS_BY_ID', payload: json.data })
    } catch (error) {
        return console.log(error)
    }
}

export function prueba() {
    return function(dispatch) {
        dispatch({
            type: REQUEST_PRUEBA,
            payload: ["Cambió la prueba :D"]
        })
    }
};
