import React from 'react'
import PokemonsCard from './PokemonsCard.jsx'
import { searchPokemonsByName } from '../store/actions/index.js'
import { useSelector, useDispatch } from 'react-redux'
import { useEffect, useState } from 'react'

export default function SearchBar() {
  let pokemonSearched = useSelector((state) => state.pokemons)
  let dispatch = useDispatch()
  useEffect(() => {
    dispatch(searchPokemonsByName())
  }, [dispatch])
  console.log(pokemonSearched)
  return (
    <div>
      <form>
        <input type="text" placeholder="Pokemon..." />
        <input type="submit" value="Search" />
      </form>

      <PokemonsCard name={pokemonSearched.name} image={pokemonSearched.image} type={pokemonSearched} />
    </div>
  )
}
