import React, { useEffect } from 'react'
import SearchBar from './SearchBar.jsx'
import { Link } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { AttackHigh, AttackLow, getTypes, byType, filterAZ, filterZA} from '../store/actions/index.js'
import { useSelector } from 'react-redux'

export default function Nav() {
  let allTypes = useSelector((state) => state.types)
  let dispatch = useDispatch()
  useEffect(() => {
    dispatch(getTypes())
  }, [dispatch])


  function firstFilter(event) {
    event.preventDefault()
    if (event.target.value === "Attack+") {
    dispatch(AttackHigh())
  }
    else if (event.target.value === "Attack-") {
      dispatch(AttackLow())
    }
  }

  function filterType(event) {
    event.preventDefault()
    dispatch(byType(event.target.value.toString().toLowerCase()))
  }

  function filterPokemons(event) {
    event.preventDefault()
    console.log(event.target.value)
  }

  function filterAZ(e) {
    e.preventDefault()
    if(e.target.value = 'A - Z') {
      dispatch(filterAZ())
    }

    else {
      dispatch(filterZA())
    }
    
  }
  
  return (

    <div>
        <Link to='/create'><button>Create your own Pokemon</button></Link>
        
        <select onChange={(event) => firstFilter(event)}>
          <option enable>Order Attack by...</option>
          <option value="Attack+">highest to lowest </option>
          <option value="Attack-">lowest to highest </option>
        </select>

        <select onChange={(e) => filterAZ(e)}>
          <option enable>Order Attack by...</option>
          <option value="A - Z"> A - Z </option>
          <option value="Z - A"> Z - A </option>
        </select>


        <select defaultValue='Order By...' onChange={filterPokemons}>
          <option value="all">All</option>
          <option value="Created by you">Created by you</option>
        </select>

          
        <select onChange={(e) => filterType(e)}>
          <option enable>Order By type...</option>
          {allTypes.map(types => {
            return <option value={types.type.toLocaleUpperCase()}>{types.type.toLocaleUpperCase()}</option>
          })}
        </select>
        
  
        <div>
            <SearchBar/>
        </div>
    
    </div>


  )
}
