import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { useEffect } from 'react'
import { searchPokemonsById } from '../store/actions'
import { Link } from 'react-router-dom'

export default  function  Details(props) {
  let details = useSelector((state) => state.details)
  let dispatch = useDispatch()
  var num = props.id
  function up() {
    num = props.id+1
  }
  function down() {
    num = props.id-1
  }
  useEffect(() =>  {
    dispatch(searchPokemonsById(num))
  }, [dispatch])
  console.log(details)
  return (
    <div>   
       {/*  */}
        <Link to='/home'><button>GO BACK</button></Link>
        <h1>{details[0].name.toUpperCase()}</h1> 
        <h1>ID: {details[0].id}#</h1>
        <img src={details[0].image} alt="NOT FOUND" width="400" height="400" />
        <p> Defense: {details[0].defense}</p>
        <p> Attack: {details[0].attack}</p>
        <p> Height: {details[0].height}</p>
        <p> Weight: {details[0].weight}</p>
        <p> Speed: {details[0].speed}</p>
        <p>Type: {details[0].type[0].toUpperCase()} , {details[0].type[1].toUpperCase()}</p> 
        <button onClick={up}>UP</button>
        <button onClick={down}>DOWN</button>
    </div>
  )
}
