import { REQUEST_POKEMONS, REQUEST_POKEMONS_BY_ID, REQUEST_POKEMONS_BY_NAME, REQUEST_ASC_DSC} from "../actions";

let initialState = {
    pokemons: [],
    types: [],
    details: []
}

export default function reducer(state = initialState, action) {
    switch(action.type) {
        case REQUEST_POKEMONS:
            return {
                ...state,
                pokemons: action.payload.data
            }
        case REQUEST_POKEMONS_BY_ID: 
            return {
                ...state,
                details: action.payload.data
            }
        case REQUEST_POKEMONS_BY_NAME:
            return {
                ...state,
                pokemons: action.payload
            }
        case REQUEST_ASC_DSC:
            return {
                ...state,
                pokemons: action.payload.data.sort(((a, b) => a.attack - b.attack))
            }
        
        default: {
            return state
        }
    }
}
