import { REQUEST_POKEMONS, 
        REQUEST_POKEMONS_BY_ID, 
        REQUEST_POKEMONS_BY_NAME, 
        FILTER_BY_ATTACK_HIGH, 
        FILTER_BY_ATTACK_LOW,
        REQUEST_TYPES,
        CLEAN_DATA,
        BY_TYPE,
        ORDER_AZ,
        ORDER_ZA
}   from "../actions";

let initialState = {
    pokemons: [],
    allPokemonsCopy: [],
    types: [],
    details: []
}

export default function reducer(state = initialState, action) {
    switch(action.type) {
        case REQUEST_POKEMONS:
            return {
                ...state,
                pokemons: action.payload.data,
                allPokemonsCopy: action.payload.data
            }
        case REQUEST_POKEMONS_BY_ID: 
            return {
                ...state,
                details: action.payload.data
            }
        case REQUEST_POKEMONS_BY_NAME:
            return {
                ...state,
                pokemons: action.payload.data
            }

        case REQUEST_TYPES:
            return {
                ...state,
                types: action.payload.data
            }

        case FILTER_BY_ATTACK_HIGH:
            let byAttack = state.allPokemonsCopy.sort((b, a) => a.attack - b.attack)
            return {
                ...state,
                pokemons: [...byAttack]
            }

        case FILTER_BY_ATTACK_LOW: 
            let byASC = state.allPokemonsCopy.sort((a, b) => a.attack - b.attack)
            
            return {
                ...state,
                pokemons: [...byASC]
            }

            case ORDER_AZ:
                let az = state.allPokemonsCopy
                let orderAZ = az.sort((a, b) => a.name - b.name)

                return {
                    ...state,
                    pokemons: [...orderAZ]
                }
            case ORDER_ZA: {
                let za = state.allPokemonsCopy
                let orderZA= za.sort((b, a) => a.name - b.name)
                return {
                    ...state,
                    pokemos: [action.payload]
                }
            }


        case BY_TYPE:
        let filter = state.allPokemonsCopy.filter((e) => e.type)
        let ByType = filter.filter(e => e.type.includes(action.payload))
        
            return {
                ...state,
                pokemons: ByType
            }
        case CLEAN_DATA:
                return {
                    ...state,
                    details: action.payload,
                };
        
        default: {
            return state;
        }
    }
}
