import { REQUEST_POKEMONS, REQUEST_POKEMONS_BY_ID, REQUEST_POKEMONS_BY_NAME, FILTER_BY_ATTACK, CLEAN_DATA} from "../actions";

let initialState = {
    pokemons: [],
    allPokemonsCopy: [],
    types: [],
    details: []
}

export default function reducer(state = initialState, action) {
    switch(action.type) {
        case REQUEST_POKEMONS:
            return {
                ...state,
                pokemons: action.payload.data,
                allPokemonsCopy: action.payload.data
            }
        case REQUEST_POKEMONS_BY_ID: 
            return {
                ...state,
                details: action.payload.data
            }
        case REQUEST_POKEMONS_BY_NAME:
            return {
                ...state,
                pokemons: action.payload.data
            }
        case FILTER_BY_ATTACK:
            let byAttack = state.allPokemonsCopy
            let filter = byAttack.sort((b, a) => a.attack - b.attack)
            
            return {
                ...state,
                pokemons: [filter]
            }
        case CLEAN_DATA:
                return {
                    ...state,
                    details: action.payload,
                };
        
        default: {
            return state
        }
    }
}
